CREATE TABLE "public"."user" ("id" serial NOT NULL, "firstname" text NOT NULL, "lastname" text NOT NULL, "email" text NOT NULL, "password" text NOT NULL, "fcm_token" text, "birthday" date, "avatar_url" text, "nation" integer, "city" integer, "district" integer, "address" text, "phone" text, "unconfirm_email" text, "created_at" timestamptz NOT NULL DEFAULT now(), "created_by" integer NOT NULL, "status" integer NOT NULL DEFAULT 0, "type" integer, PRIMARY KEY ("id") , UNIQUE ("email"));
