CREATE TABLE "public"."news" ("id" serial NOT NULL, "title" text NOT NULL, "content" Text NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), "created_by" integer NOT NULL, "is_soft_deleted" boolean NOT NULL, "admin_only_flag" boolean NOT NULL DEFAULT false, PRIMARY KEY ("id") , FOREIGN KEY ("created_by") REFERENCES "public"."admin"("id") ON UPDATE restrict ON DELETE cascade);
