CREATE TABLE "public"."projects" ("id" serial NOT NULL, "name" text NOT NULL, "leader" integer, "details" text NOT NULL, "parent_project" integer, "status" integer NOT NULL DEFAULT 0, "created_at" timestamptz NOT NULL, "created_by" integer NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("leader") REFERENCES "public"."admin"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("created_by") REFERENCES "public"."admin"("id") ON UPDATE restrict ON DELETE cascade);
